<?php if ( !function_exists('dite_zoom_pluginurl') ){	function dite_zoom_pluginurl($param = ''){		if($param == 'css' || $param == 'js' || $param == 'images') $path=$param;		else $path = '';		return plugin_dir_url(dirname(__FILE__)).$path;	}}if ( !function_exists('dite_zoom_pluginpath') ){	function dite_zoom_pluginpath(){		return plugin_dir_path(dirname(__FILE__) );	}}if ( !function_exists('dite_zoom_fullurl') ){	function dite_zoom_fullurl(){		if(!isset($_SERVER['REQUEST_URI'])){			$serverrequri = $_SERVER['PHP_SELF'];		}else{			$serverrequri =    $_SERVER['REQUEST_URI'];		}		$s = empty($_SERVER["HTTPS"]) ? '' : ($_SERVER["HTTPS"] == "on") ? "s" : "";		$protocol = LA_strleft(strtolower($_SERVER["SERVER_PROTOCOL"]), "/").$s;		$port = ($_SERVER["SERVER_PORT"] == "80") ? "" : (":".$_SERVER["SERVER_PORT"]);		return $protocol."://".$_SERVER['SERVER_NAME'].$port.$serverrequri;   	}}?>