<?php$zoom_post = get_post($id);if(count($zoom_post) > 0) :	$thumb = wp_get_attachment_image_src( get_post_thumbnail_id($id), 'medium' );	$original = wp_get_attachment_image_src( get_post_thumbnail_id($id), 'full' );	$url = $thumb['0']; ?>	<style >		.dite_zoom img{			max-width:none !important;		}	</style>	<script type="text/javascript">		jQuery(document).ready(function($) {			$('.jqzoom').jqzoom({					zoomType: 'standard',					lens:true,					position: "right",					preloadImages: true,					alwaysOn:false				});					});	</script>	<div class="dite_zoom">		<a href="<?php echo $original[0] ?>" class="jqzoom" rel='gal1'  title="<?php echo $zoom_post->post_title; ?>" >			<img src="<?php echo $url ?>" title="<?php echo $zoom_post->post_title; ?>" style="border: 4px solid #666;" />		</a>	</div><?php endif; ?>